const items_layout=document.querySelector(".items-layout");let items_array=[],first_card=null,second_card=null,isAlreadyTwoFlipped=!1;function renderItems(items){items.forEach(item=>{items_layout.innerHTML+=`<div class = "item-card" data-name="${item.image_link}">\n                <img class="front-cover" src=${item.image_link} />\n                <img class="back-cover" src="images/back-cover.jpeg" />\n            </div>`}),shuffleAndClickEventOfItem(document.querySelectorAll(".item-card"))}function shuffleAndClickEventOfItem(itemCards){itemCards.forEach(itemCard=>{itemCard.style.order=Math.floor(Math.random()*itemCards.length),itemCard.onclick=function(){flipEvent(itemCard)}})}function flipEvent(itemCard){!0!==isAlreadyTwoFlipped&&itemCard!==first_card&&(itemCard.classList.add("flip"),null===first_card?first_card=itemCard:null===second_card&&(second_card=itemCard,isAlreadyTwoFlipped=!0,doSomeMatchCheck()))}function doSomeMatchCheck(){first_card.dataset.name===second_card.dataset.name?(first_card.onclick=null,second_card.onclick=null,setTimeout(()=>{alert("You Did It!")},1e3)):setTimeout(()=>{first_card.classList.remove("flip"),second_card.classList.remove("flip")},1e3),setTimeout((function(){first_card=null,second_card=null,isAlreadyTwoFlipped=!1}),1500)}function readJSONfile(fileName){fetch(fileName).then(response=>response.json()).then(data=>{items_array=data.items,renderItems(items_array)})}readJSONfile("seeders/data.json");